{
  "description": "Notify STH-Comet of all Motion Sensor count changes", // Descrição da notificação
  "subject": {
    "entities": [
      {
        "id": "urn:ngsi-ld:Lamp:001",
        "type": "Lamp"
      }
    ],
    "condition": { "attrs": ["luminosity"] } // Condição para acionar a notificação (alteração de luminosidade)
  },
  "notification": {
    "http": {
      "url": "http://{{url}}:8666/notify" // URL para notificação do STH-Comet
    },
    "attrs": [
      "luminosity" // Atributo a ser notificado (luminosidade)
    ],
    "attrsFormat": "legacy" // Formato dos atributos a ser notificado (legado)
  }
}
